import '../globals.scss';
import React, { useEffect } from 'react';

export default function App({ Component, pageProps }) {
  const pageWrapper = React.useRef(null);

  React.useEffect(() => {
    pageWrapper.current.style.visibility = 'visible';
    fadeIn();
    return function cleanup() {
      fadeOut();
    }
  });

  function fadeIn() {
    pageWrapper.current.style.opacity = "1";
  }

  function fadeOut() {
    pageWrapper.current.style.opacity = "0";
  }

  return (
    <div id="page_wrapper" ref={pageWrapper}>
      <Component 
        {...pageProps} 
      />
    </div>
  )
}
