import Link from 'next/link'
import styles from './styles.module.scss';

function Button({
    text,
    downArrow,
    url,
}) {

    const imgSrc = 'asset/button-arrow.svg';
    if (downArrow == 'true') {
        imgSrc = 'asset/button-down-arrow.svg';
    }

    return (
        <Link href={url}>
            <a className={styles.button_container}>
                <p>{text}</p>
                <img src={imgSrc} alt=''/>
            </a>
        </Link>
    )
}

export default Button;
