import '../globals.scss';
import React from 'react';

export default function App({ Component, pageProps }) {
  const pageWrapper = React.createRef();
  React.useEffect(() => {
    fadeIn();
  }, [])

  function fadeIn() {
    setTimeout(() => {
        pageWrapper.current.style.opacity = '1';
    }, 800)
  }

  fadeIn();
  return (
    <div id='page_wrapper' ref={pageWrapper}>
      <Component 
        {...pageProps} 
      />
    </div>
  )
}
