import Link from 'next/link'
import styles from './styles.module.scss';

function Button({
    text,
    downArrow,
    goBack,
    url,
}) {

    const imgSrc = 'asset/button-arrow.svg';
    const regular = styles.button_container;
    const back = styles.button_container_back;
    let useClass;

    if (downArrow == 'true') {
        imgSrc = 'asset/button-down-arrow.svg';
    }
    if (goBack == 'true') {
        imgSrc = 'asset/button-back-arrow.svg';
        useClass = back;
    }


    return (
        <Link href={url}>
            <a className={styles.button_container}>
                <p>{text}</p>
                <img src={imgSrc} alt=''/>
            </a>
        </Link>
    )
}

export default Button;
